mapper.select:
select id, member_id, review_id, recommend_state
from recommend
where member_id = #{memberId}
  and review_id = #{reviewId}

mapper.update:
update recommend
set recommend_state=#{recommend.recommendState}
where member_id = #{memberId}
  and review_id = #{recommend.reviewId}

mapper.delete:
delete
from recommend
where member_id = #{memberId}
  and review_id = #{reviewId}
