mapper.update:
update review
set like_count=like_count+1
where id = #{reviewId}

update review
set dislike_count=dislike_count+1
where id = #{reviewId}

update review
set like_count=like_count-1
where id = #{reviewId}

update review
set dislike_count=dislike_count-1
where id = #{reviewId}

mapper.select:
select like_count
from review
where id=#{reviewId}

select count (*)
from review
where movie_id = #{movieId}

select avg (rating)
from review
where movie_id = #{movieId}

SELECT COUNT(*)
FROM review
WHERE member_id = #{memberId}
